package flow_control;
import java.util.*;

public class task_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		char c=sc.next().charAt(0);
		
		if(c=='r') System.out.print("RED");
		else if(c=='b') System.out.print("BLUE");
		else if(c=='g') System.out.print("GREEN");
		else if(c=='o') System.out.print("ORANGE");
		else if(c=='y') System.out.print("YELLOW");
		else if(c=='w') System.out.print("WHITE");
	}

}
